import React, { useEffect, useState } from "react";
import axios from "axios";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import CourseCard from "../components/CourseCard";

const Home = () => {
  const [featuredCourses, setFeaturedCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeaturedCourses = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/courses/featured");
        setFeaturedCourses(response.data);
      } catch (error) {
        console.error("Error fetching featured courses:", error);
      } finally {
        setLoading(false);
      }
    };
    
    fetchFeaturedCourses();
  }, []);

  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[60vh]">
        <div className="loading-spinner" />
      </div>
    );
  }

  return (
    <div className="space-y-16 py-8">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-r from-primary-600 to-primary-800 text-white py-20">
        <div className="container mx-auto px-4">
          <div className="max-w-3xl">
            <h1 className="text-4xl md:text-5xl font-bold mb-6">
              Learn New Skills Online
            </h1>
            <p className="text-xl mb-8 text-primary-100">
              Discover high-quality courses from expert instructors. Start learning today and transform your career.
            </p>
            <a href="/courses" className="btn-primary bg-white text-primary-600 hover:bg-primary-50">
              Browse Courses
            </a>
          </div>
        </div>
      </section>

      {/* Featured Courses Section */}
      <section className="container mx-auto px-4">
        <h2 className="text-3xl font-bold text-center mb-12">Featured Courses</h2>
        {featuredCourses.length > 0 ? (
          <Slider {...carouselSettings} className="max-w-5xl mx-auto">
            {featuredCourses.map((course) => (
              <div key={course.id} className="px-2">
                <div className="bg-white rounded-lg shadow-lg overflow-hidden">
                  <div className="relative h-96">
                    <img 
                      src={course.imageUrl} 
                      alt={course.title} 
                      className="w-full h-full object-cover"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent" />
                    <div className="absolute bottom-0 left-0 right-0 p-6 text-white">
                      <span className="inline-block bg-primary-600 text-white px-3 py-1 rounded-full text-sm mb-3">
                        {course.category}
                      </span>
                      <h3 className="text-2xl font-bold mb-2">{course.title}</h3>
                      <p className="text-lg mb-4 line-clamp-2">{course.description}</p>
                      <div className="flex items-center justify-between">
                        <span className="text-2xl font-bold">${course.price}</span>
                        <a 
                          href={`/course/${course.id}`}
                          className="btn-primary bg-white text-primary-600 hover:bg-primary-50"
                        >
                          Learn More
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </Slider>
        ) : (
          <p className="text-center text-gray-500">No featured courses available</p>
        )}
      </section>

      {/* Features Section */}
      <section className="bg-gray-50 py-16">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-12">Why Choose Our Platform?</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="bg-white p-6 rounded-lg shadow-md text-center">
              <div className="text-primary-500 text-4xl mb-4">🎓</div>
              <h3 className="text-xl font-semibold mb-2">Expert Instructors</h3>
              <p className="text-gray-600">Learn from industry professionals with real-world experience.</p>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-md text-center">
              <div className="text-primary-500 text-4xl mb-4">🔄</div>
              <h3 className="text-xl font-semibold mb-2">Lifetime Access</h3>
              <p className="text-gray-600">Get lifetime access to all course materials after purchase.</p>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-md text-center">
              <div className="text-primary-500 text-4xl mb-4">💼</div>
              <h3 className="text-xl font-semibold mb-2">Practical Skills</h3>
              <p className="text-gray-600">Gain skills you can immediately apply in your career.</p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="container mx-auto px-4 text-center">
        <h2 className="text-3xl font-bold mb-6">Ready to Start Learning?</h2>
        <p className="text-xl text-gray-600 mb-8">
          Join thousands of students who are already learning on our platform.
        </p>
        <a href="/courses" className="btn-primary">
          Browse All Courses
        </a>
      </section>
    </div>
  );
};

export default Home;
