import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import CourseCard from '../components/CourseCard';

const Home = () => {
  const [featuredCourses, setFeaturedCourses] = useState([]);
  const [popularCategories, setPopularCategories] = useState([]);

  useEffect(() => {
    // Simulated API call for featured courses
    const fetchFeaturedCourses = async () => {
      // This would be replaced with an actual API call
      const courses = [
        {
          id: 1,
          title: "Web Development Bootcamp",
          description: "Learn modern web development with React, Node.js, and MongoDB",
          instructor: "John Doe",
          price: 99.99,
          rating: 4.8,
          imageUrl: "https://images.unsplash.com/photo-1498050108023-c5249f4df085?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60",
          category: "Programming",
          level: "Beginner",
          duration: "8 weeks",
          featured: true
        },
        {
          id: 2,
          title: "Data Science Fundamentals",
          description: "Master the basics of data science and machine learning",
          instructor: "Jane Smith",
          price: 89.99,
          rating: 4.6,
          imageUrl: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60",
          category: "Data Science",
          level: "Intermediate",
          duration: "10 weeks",
          featured: true
        },
        {
          id: 3,
          title: "Digital Marketing Mastery",
          description: "Learn modern digital marketing strategies and tools",
          instructor: "Mike Johnson",
          price: 79.99,
          rating: 4.7,
          imageUrl: "https://images.unsplash.com/photo-1557838923-2985c318be48?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60",
          category: "Marketing",
          level: "Beginner",
          duration: "6 weeks",
          featured: true
        }
      ];
      setFeaturedCourses(courses);
    };

    // Simulated API call for popular categories
    const fetchPopularCategories = async () => {
      const categories = [
        { id: 1, name: "Programming", count: 150 },
        { id: 2, name: "Data Science", count: 120 },
        { id: 3, name: "Business", count: 100 },
        { id: 4, name: "Marketing", count: 80 },
        { id: 5, name: "Design", count: 70 },
        { id: 6, name: "Personal Development", count: 60 }
      ];
      setPopularCategories(categories);
    };

    fetchFeaturedCourses();
    fetchPopularCategories();
  }, []);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="bg-blue-600 text-white">
        <div className="container mx-auto px-4 py-20">
          <div className="max-w-3xl">
            <h1 className="text-4xl md:text-5xl font-bold mb-6">
              Learn New Skills Online with Expert Instructors
            </h1>
            <p className="text-xl mb-8">
              Access high-quality courses from industry experts and transform your career today.
            </p>
            <div className="flex space-x-4">
              <Link
                to="/courses"
                className="bg-white text-blue-600 px-8 py-3 rounded-full font-semibold hover:bg-blue-50 transition-colors"
              >
                Browse Courses
              </Link>
              <Link
                to="/about"
                className="border-2 border-white text-white px-8 py-3 rounded-full font-semibold hover:bg-white hover:text-blue-600 transition-colors"
              >
                Learn More
              </Link>
            </div>
          </div>
        </div>
      </div>

      {/* Featured Courses Section */}
      <div className="container mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold mb-8">Featured Courses</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {featuredCourses.map(course => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>
      </div>

      {/* Popular Categories Section */}
      <div className="bg-white py-16">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-8">Popular Categories</h2>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
            {popularCategories.map(category => (
              <Link
                key={category.id}
                to={`/courses?category=${category.name}`}
                className="bg-gray-50 p-4 rounded-lg text-center hover:bg-blue-50 transition-colors"
              >
                <h3 className="font-semibold text-gray-800">{category.name}</h3>
                <p className="text-sm text-gray-600">{category.count} courses</p>
              </Link>
            ))}
          </div>
        </div>
      </div>

      {/* CTA Section */}
      <div className="container mx-auto px-4 py-16">
        <div className="bg-blue-600 rounded-lg p-8 text-white text-center">
          <h2 className="text-3xl font-bold mb-4">Ready to Start Learning?</h2>
          <p className="text-xl mb-8">
            Join thousands of learners who are already transforming their careers
          </p>
          <Link
            to="/courses"
            className="bg-white text-blue-600 px-8 py-3 rounded-full font-semibold hover:bg-blue-50 transition-colors inline-block"
          >
            Get Started Today
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Home;
