import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import CourseCard from '../components/CourseCard';

const SearchResults = () => {
  const [searchParams] = useSearchParams();
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const query = searchParams.get('q') || '';
    
    // Simulated API call for search results
    const searchCourses = async () => {
      setLoading(true);
      try {
        // This would be replaced with an actual API call
        const allCourses = [
          {
            id: 1,
            title: "Web Development Bootcamp",
            description: "Learn modern web development with React, Node.js, and MongoDB",
            instructor: "John Doe",
            price: 99.99,
            rating: 4.8,
            imageUrl: "https://images.unsplash.com/photo-1498050108023-c5249f4df085?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60",
            category: "Programming",
            level: "Beginner",
            duration: "8 weeks",
            featured: true
          },
          {
            id: 2,
            title: "Data Science Fundamentals",
            description: "Master the basics of data science and machine learning",
            instructor: "Jane Smith",
            price: 89.99,
            rating: 4.6,
            imageUrl: "https://images.unsplash.com/photo-1551288049-bebda4e38f71?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60",
            category: "Data Science",
            level: "Intermediate",
            duration: "10 weeks",
            featured: true
          },
          {
            id: 3,
            title: "Digital Marketing Mastery",
            description: "Learn modern digital marketing strategies and tools",
            instructor: "Mike Johnson",
            price: 79.99,
            rating: 4.7,
            imageUrl: "https://images.unsplash.com/photo-1557838923-2985c318be48?ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60",
            category: "Marketing",
            level: "Beginner",
            duration: "6 weeks",
            featured: true
          }
        ];

        // Smart search implementation
        const results = allCourses.filter(course => {
          const searchTerms = query.toLowerCase().split(' ');
          return searchTerms.every(term => 
            course.title.toLowerCase().includes(term) ||
            course.description.toLowerCase().includes(term) ||
            course.category.toLowerCase().includes(term) ||
            course.instructor.toLowerCase().includes(term)
          );
        });

        setSearchResults(results);
        setError(null);
      } catch (err) {
        setError('Failed to fetch search results. Please try again.');
        setSearchResults([]);
      } finally {
        setLoading(false);
      }
    };

    if (query) {
      searchCourses();
    } else {
      setSearchResults([]);
      setLoading(false);
    }
  }, [searchParams]);

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">
        Search Results for "{searchParams.get('q') || ''}"
      </h1>

      {loading && (
        <div className="text-center py-8">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
        </div>
      )}

      {error && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded-lg">
          {error}
        </div>
      )}

      {!loading && !error && searchResults.length === 0 && (
        <div className="text-center py-8">
          <p className="text-gray-600">No courses found matching your search criteria.</p>
        </div>
      )}

      {!loading && !error && searchResults.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {searchResults.map(course => (
            <CourseCard key={course.id} course={course} />
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchResults;
