import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const CourseDetails = () => {
    const { id } = useParams();
    const [course, setCourse] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchCourseDetails = async () => {
            try {
                setLoading(true);
                const response = await fetch(`http://localhost:8080/api/courses/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch course details');
                }
                const data = await response.json();
                setCourse(data);
            } catch (err) {
                setError(err.message);
                console.error('Error fetching course details:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchCourseDetails();
    }, [id]);

    if (loading) {
        return (
            <div className="flex justify-center items-center min-h-screen">
                <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
            </div>
        );
    }

    if (error) {
        return (
            <div className="flex justify-center items-center min-h-screen">
                <div className="text-red-500 text-xl">{error}</div>
            </div>
        );
    }

    if (!course) {
        return (
            <div className="flex justify-center items-center min-h-screen">
                <div className="text-gray-500 text-xl">Course not found</div>
            </div>
        );
    }

    return (
        <div className="container mx-auto px-4 py-8">
            <div className="max-w-4xl mx-auto">
                <div className="bg-white rounded-lg shadow-lg overflow-hidden">
                    <img
                        src={course.imageUrl}
                        alt={course.title}
                        className="w-full h-96 object-cover"
                    />
                    <div className="p-8">
                        <h1 className="text-3xl font-bold text-gray-800 mb-4">{course.title}</h1>
                        <div className="flex items-center gap-4 mb-6">
                            <span className="text-lg font-bold text-blue-600">${course.price}</span>
                            <span className="text-gray-500">{course.duration}</span>
                            <span className="text-gray-500">{course.level}</span>
                            <span className="text-gray-500">{course.category}</span>
                        </div>
                        <p className="text-gray-600 text-lg mb-8">{course.description}</p>
                        <button className="w-full bg-blue-500 text-white py-3 rounded-lg text-lg font-semibold hover:bg-blue-600 transition-colors">
                            Enroll Now
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CourseDetails;
