import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <nav className="bg-blue-600 text-white shadow-md">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold">
          EduPlatform
        </Link>

        {/* Desktop Menu */}
        <div className="hidden md:flex space-x-6">
          <Link to="/" className="hover:text-gray-200">Home</Link>
          <Link to="/courses" className="hover:text-gray-200">Courses</Link>
          <Link to="/search" className="hover:text-gray-200">Search</Link>
          <Link to="/about" className="hover:text-gray-200">About</Link>
        </div>

        {/* Mobile Menu Button */}
        <button 
          className="md:hidden focus:outline-none" 
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? (
            <span className="text-3xl">&times;</span> // Close Icon
          ) : (
            <span className="text-3xl">&#9776;</span> // Menu Icon
          )}
        </button>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="md:hidden bg-blue-700 py-3">
          <Link to="/" className="block px-4 py-2 hover:bg-blue-500" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/courses" className="block px-4 py-2 hover:bg-blue-500" onClick={() => setMenuOpen(false)}>Courses</Link>
          <Link to="/search" className="block px-4 py-2 hover:bg-blue-500" onClick={() => setMenuOpen(false)}>Search</Link>
          <Link to="/about" className="block px-4 py-2 hover:bg-blue-500" onClick={() => setMenuOpen(false)}>About</Link>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
