import React from "react";
import { Link } from "react-router-dom";

const Footer = () => {
  return (
    <footer className="bg-gray-900 text-white py-6">
      <div className="container mx-auto px-4 text-center">
        <p className="text-lg font-semibold">EduPlatform</p>
        <p className="text-sm text-gray-400">Learn & Grow with the Best Online Courses</p>

        {/* Navigation Links */}
        <div className="mt-3 flex justify-center space-x-6">
          <Link to="/" className="hover:text-gray-400">Home</Link>
          <Link to="/courses" className="hover:text-gray-400">Courses</Link>
          <Link to="/about" className="hover:text-gray-400">About</Link>
          <Link to="/contact" className="hover:text-gray-400">Contact</Link>
        </div>

        {/* Social Media Links */}
        <div className="mt-4 flex justify-center space-x-4">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" className="hover:text-blue-400">
            Facebook
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" className="hover:text-blue-400">
            Twitter
          </a>
          <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" className="hover:text-blue-400">
            LinkedIn
          </a>
        </div>

        {/* Copyright */}
        <p className="mt-4 text-sm text-gray-500">&copy; {new Date().getFullYear()} EduPlatform. All rights reserved.</p>
      </div>
    </footer>
  );
};

export default Footer;
