import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { Container, Grid, Typography, Card, CardContent, CardMedia, Button } from '@mui/material';

const Home = () => {
    const [featuredCourses, setFeaturedCourses] = useState([]);
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [coursesRes, categoriesRes] = await Promise.all([
                    axios.get('http://localhost:8080/api/courses/featured'),
                    axios.get('http://localhost:8080/api/categories')
                ]);
                setFeaturedCourses(coursesRes.data);
                setCategories(categoriesRes.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };
        fetchData();
    }, []);

    return (
        <Container maxWidth="lg" sx={{ py: 4 }}>
            {/* Hero Section */}
            <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
                <Typography variant="h2" component="h1" gutterBottom>
                    Welcome to Our Learning Platform
                </Typography>
                <Typography variant="h5" color="textSecondary" paragraph>
                    Discover top-quality courses and start learning today
                </Typography>
            </div>

            {/* Featured Courses Section */}
            <Typography variant="h4" component="h2" gutterBottom sx={{ mb: 3 }}>
                Featured Courses
            </Typography>
            <Grid container spacing={4} sx={{ mb: 6 }}>
                {featuredCourses.map((course) => (
                    <Grid item key={course.id} xs={12} sm={6} md={4}>
                        <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                            <CardMedia
                                component="img"
                                height="140"
                                image={course.imageUrl || 'https://source.unsplash.com/random?education'}
                                alt={course.title}
                            />
                            <CardContent sx={{ flexGrow: 1 }}>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {course.title}
                                </Typography>
                                <Typography>
                                    {course.description.substring(0, 100)}...
                                </Typography>
                                <Typography variant="h6" color="primary" sx={{ mt: 2 }}>
                                    ${course.price}
                                </Typography>
                            </CardContent>
                            <Button 
                                component={Link} 
                                to={`/courses/${course.id}`}
                                variant="contained" 
                                color="primary"
                                sx={{ m: 2 }}
                            >
                                Learn More
                            </Button>
                        </Card>
                    </Grid>
                ))}
            </Grid>

            {/* Categories Section */}
            <Typography variant="h4" component="h2" gutterBottom sx={{ mb: 3 }}>
                Browse by Category
            </Typography>
            <Grid container spacing={4}>
                {categories.map((category) => (
                    <Grid item key={category.id} xs={12} sm={6} md={3}>
                        <Card 
                            component={Link} 
                            to={`/courses?category=${category.name}`}
                            sx={{ 
                                height: '100%', 
                                textDecoration: 'none',
                                '&:hover': {
                                    transform: 'scale(1.02)',
                                    transition: 'transform 0.2s ease-in-out'
                                }
                            }}
                        >
                            <CardContent>
                                <Typography gutterBottom variant="h5" component="h2">
                                    {category.name}
                                </Typography>
                                <Typography color="textSecondary">
                                    {category.description}
                                </Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default Home;
